import { Component, Input, OnInit } from '@angular/core';
import { ICard } from 'src/app/interfaces/Icard';
import { Ideck } from 'src/app/interfaces/Ideck';

@Component({
  selector: 'app-stats',
  templateUrl: './stats.component.html',
  styleUrls: ['./stats.component.scss']
})
export class StatsComponent implements OnInit {

  @Input() allCards: ICard = {
    count: 0,
    data: [],
    page: 0,
    pageSize: 0,
    totalCount: 0
  };

  @Input() decks: Array<Ideck> = [];

  constructor() {}

  ngOnInit(): void {}

}
<div class="grid space-x-1 lg:grid-cols-6 p-4 gap-2">
  <div class="bg-white border-2 border-gray-400 rounded">
    <div class="bg-white">
      <div class="px-6 py-4">
        <div class="font-bold text-xl mb-2">Number of Cards</div>
        <p class="text-gray-700 text-sm">
          <span class="font-bold">
            {{ allCards.count }}
          </span>
        </p>
      </div>
    </div>
  </div>
  <div class="bg-white border-2 border-gray-400 rounded">
    <div class="bg-white">
      <div class="px-6 py-4">
        <div class="font-bold text-xl mb-2">Number of Decks</div>
        <p class="text-gray-700 text-sm">
          <span class="font-bold">
            {{ decks.length }}
          </span>
        </p>
      </div>
    </div>
  </div>
</div>
