import { Component, OnInit } from '@angular/core';

import { Ideck } from 'src/app/interfaces/Ideck';

@Component({
  selector: 'app-deckdetails',
  templateUrl: './deckdetails.component.html',
  styleUrls: ['./deckdetails.component.scss']
})
export class DeckDetailsComponent implements OnInit {

  public deck: Ideck = {
    id: 0,
    cards: [],
    name: '',
    userEmail: ''
  };

  constructor() {}

  ngOnInit(): void {}

}
<h2 mat-dialog-title>Details</h2>
<mat-dialog-content class="mat-typography hidden">
  <div class="grid space-x-1 lg:grid-cols-6 p-4 gap-2">
    <div *ngFor="let card of deck.cards" class="bg-white border-2 border-gray-400 rounded">
      <div class="bg-white">
        <img class="w-full" src="{{card.images.small}}" alt="{{card.name}}">
        <div class="px-6 py-4">
          <div class="font-bold text-xl mb-2">{{card.name}}</div>
          <p class="text-gray-700 text-base text-xs">
            Level: {{card.level}}
          </p>
          <p class="text-gray-700 text-base">
            Level: {{card.hp}}
          </p>
          <p class="text-gray-700 text-base">
            Level: {{card.evolvesFrom}}
          </p>
        </div>
      </div>
    </div>
  </div>
</mat-dialog-content>
<mat-dialog-actions align="end">
  <button mat-button mat-dialog-close>Close</button>
</mat-dialog-actions>
