import { Component, OnInit, ViewChild } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { DashboardComponent } from 'src/app/pages/dashboard/dashboard.component';

@Component({
  selector: 'app-leftmenu',
  templateUrl: './leftmenu.component.html',
  styleUrls: ['./leftmenu.component.scss']
})
export class LeftMenuComponent implements OnInit {

  @ViewChild(DashboardComponent, { static: true })
  dashboardComponent!: DashboardComponent;

  constructor(
    private route: ActivatedRoute,
    private router: Router
  ) { }

  ngOnInit(): void {
  }

  deckOfCards() {
    this.dashboardComponent.isDashboard = false;
    this.router.navigate(['deckofcards'], {relativeTo:this.route});
  }

}
<div class="flex flex-col items-center w-16 h-full pb-4 overflow-auto border-r border-gray-300">
  <a class="flex items-center justify-center flex-shrink-0 w-full h-16 bg-gray-300">
      <img src="https://p1.hiclipart.com/preview/675/411/676/pokemon-pokemon-pikachu-png-clipart.jpg" alt="">
  </a>
  <a class="flex items-center justify-center flex-shrink-0 w-10 h-10 mt-4 rounded hover:bg-gray-300">
    <mat-icon aria-hidden="false" aria-label="Example home icon" fontIcon="home"></mat-icon>
  </a>
  <a class="flex items-center justify-center flex-shrink-0 w-10 h-10 mt-4 rounded hover:bg-gray-300" (click)="deckOfCards()">
    <mat-icon aria-hidden="false" aria-label="Cards" fontIcon="list"></mat-icon>
  </a>
</div>
