import { Component, Inject, OnInit, inject } from '@angular/core';
import { MatSnackBarRef } from '@angular/material/snack-bar';
import {MAT_SNACK_BAR_DATA} from '@angular/material/snack-bar';

@Component({
  selector: 'app-snackBar',
  templateUrl: './snackBar.component.html',
  styleUrls: ['./snackBar.component.scss']
})
export class SnackBarComponent implements OnInit {

  public snackMessage: string = '';
  snackBarRef = inject(MatSnackBarRef);

  constructor(@Inject(MAT_SNACK_BAR_DATA) public data: any) { }

  ngOnInit(): void {
  }

}
<div class="flex">
  <span matSnackBarLabel [ngClass]="data.type === 'error' ? 'bg-red-600' : 'bg-green-600'">
    {{data.message}}
  </span>
  <span matSnackBarActions class="bg-red-600">
    <button mat-button matSnackBarAction (click)="snackBarRef.dismissWithAction()">
      <mat-icon>cancel</mat-icon>
    </button>
  </span>
</div>
