import { Component, OnInit } from '@angular/core';

import { LoginService } from './service/login.service';
import { Ilogin } from 'src/app/interfaces/Ilogin';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit {

  public form!: FormGroup;

  constructor(
    private $loginService: LoginService,
    private formBuilder: FormBuilder,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.createForm();
  }

  createForm() {
    this.form = this.formBuilder.group({
      email: ['', Validators.compose([Validators.required])],
      password: ['', Validators.compose([Validators.required])]
    });
  }

  doLogin() {
    this.$loginService.getLogins().subscribe(
      (response: Array<Ilogin>) => {
        this.findingByUser(response);
      },
      (error) => {
        console.error('Error fetching logins:', error);
        alert('An error occurred while logging in. Please try again later.');
      }
    );
  }

  findingByUser(response: Array<Ilogin>) {
    const user = response.find(u => u.email === this.form.value.email && u.password === this.form.value.password);
    if (user) {
      this.loggingUser(user);
    } else {
      alert('User not found!');
    }
  }

  loggingUser(user: Ilogin) {
    localStorage.setItem('login', user.email);
    this.form.reset();
    alert('WOWWW! User Found!');
    this.router.navigate(['dashboard']);
  }
}

  <!-- <div class="sm:mx-auto sm:w-full sm:max-w-sm">
    <h2 class="mt-15 text-center text-2xl font-bold leading-9 tracking-tight text-gray-900">
      Login into account
    </h2>
  </div> -->

  <div class="mt-5 sm:mx-auto sm:w-full sm:max-w-sm">
    <mat-card>
      <mat-card-content>
        <form [formGroup]="form">
          <!-- <mat-error>
            The username and password were not recognized
          </mat-error> -->
          <mat-form-field>
            <input matInput placeholder="E-mail" id="email" formControlName="email" type="text">
            <!-- <mat-error>
              Please provide a valid email address
            </mat-error> -->
          </mat-form-field>
          <mat-form-field>
            <input matInput type="password" placeholder="Password" id="password" formControlName="password" type="password">
            <!-- <mat-error>
              Please provide a valid password
            </mat-error> -->
          </mat-form-field>
          <button
            mat-raised-button
            color="primary"
            [disabled]="!form.valid"
            (click)="doLogin()"
            >Login</button>
        </form>
      </mat-card-content>
    </mat-card>
  </div>
