import { Component, OnInit } from '@angular/core';
import { Data } from 'src/app/interfaces/Icard';

import { CardService } from '../cards/service/card.service';

import {MatTableDataSource} from '@angular/material/table';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Ideck } from 'src/app/interfaces/Ideck';
import { NewDeckFormService } from './service/newdeckform.service';


@Component({
  selector: 'app-newdeckform',
  templateUrl: './newdeckform.component.html',
  styleUrls: ['./newdeckform.component.scss']
})

export class NewdeckFormComponent implements OnInit {

  public cards: Array<Data> = [];

  public displayedColumns: string[] = ['images', 'name', 'supertype', 'level', 'hp', 'rarity', 'select'];
  public dataSource = new MatTableDataSource<Data>();

  public form!: FormGroup;

  constructor(
    private $cardsService: CardService,
    private $newDeckFormService: NewDeckFormService,
    private formBuilder: FormBuilder,
  ) { }

  ngOnInit(): void {
    this.cards = this.$cardsService.allCards;
    this.updateDataSource(this.cards);
    this.createForm();
  }

  createForm() {
    this.form = this.formBuilder.group({
      name: ['', [Validators.required, Validators.minLength(3)]],
    });
  }

  applyFilter(event: Event) {
    this.dataSource.filterPredicate = function(data, filter: string): boolean {
      return data.name.toLowerCase().includes(filter);
    };

    let filterValue = (event.target as HTMLInputElement).value;
    filterValue = filterValue.trim().toLowerCase();
    this.dataSource.filter = filterValue;
  }

  setSelectionFalseToCards(cards: Array<Data>) {
    cards.map((c: Data) => {
      c.isCardSelected = false;
    });
    return cards;
  }

  addCardToDeck(card: Data) {
    this.cards.map((c: Data) => {
      if(card.id === c.id) {
        c.isCardSelected = !c.isCardSelected;
      }
    });
    this.updateDataSource(this.cards);
  }

  updateDataSource(cards: Array<Data>) {
    // this.dataSource = new MatTableDataSource<Data>();
    // this.dataSource.data = this.cards;
    this.dataSource = new MatTableDataSource<Data>(cards);
  }

  createDeck() {
    let deck: Ideck = {
      cards: this.cards.filter((c: Data) => c.isCardSelected === true),
      name: this.form.value.name,
      userEmail: localStorage.getItem('login') || ''
    };

    this.$newDeckFormService.createNewDeck(deck).subscribe((response: any) => {
      console.log(response);
    });

  }

}
<div class="p-8">

  <!-- <mat-dialog-content class="mat-typography hidden">
    <h3>Name</h3>
    <p>{{this.deck.name}}</p>

    <h3>Number of cards</h3>
    <p>{{this.deck.cards.length}}</p>
  </mat-dialog-content>
  <mat-dialog-actions align="end">
    <button mat-button mat-dialog-close>Close</button>
  </mat-dialog-actions> -->

  <div class="grid grid-cols-1">
    <form [formGroup]="form">
      <mat-form-field>
        <input matInput placeholder="Name of Deck" id="name" formControlName="name" type="text">
        <mat-error
          *ngIf="(form.get('name')?.dirty)
          && form.get('name')?.hasError('required')">
          The field shouldn't be empty!
        </mat-error>
        <mat-error
          *ngIf="(form.get('name')?.dirty)
          && form.get('name')?.hasError('minlength')">
          The min of characters is 3!
        </mat-error>
      </mat-form-field>
      <button
        mat-raised-button
        color="primary"
        [disabled]="!form.valid"
        (click)="createDeck()"
        class="ml-3"
        >Create Deck</button>
    </form>
  </div>


  <div class="grid grid-cols-1" *ngIf="dataSource">

    <mat-form-field>
      <mat-label>Filter</mat-label>
      <input matInput (keyup)="applyFilter($event)" placeholder="Ex. ium" #input>
    </mat-form-field>

    <table mat-table [dataSource]="dataSource" class="mat-elevation-z8 w-full">

      <!-- Image Column -->
      <ng-container matColumnDef="images">
        <th mat-header-cell *matHeaderCellDef> Image </th>
        <td mat-cell *matCellDef="let element">
          <img class="rounded-full w-10 h-10" src="{{element.images.small}}" alt="{{element.name}}">
        </td>
      </ng-container>

      <!-- Name Column -->
      <ng-container matColumnDef="name">
        <th mat-header-cell *matHeaderCellDef> Name </th>
        <td mat-cell *matCellDef="let element"> {{element.name}} </td>
      </ng-container>

      <!-- Super Type Column -->
      <ng-container matColumnDef="supertype">
        <th mat-header-cell *matHeaderCellDef> Super Type </th>
        <td mat-cell *matCellDef="let element"> {{element.supertype}} </td>
      </ng-container>

      <!-- Level Column -->
      <ng-container matColumnDef="level">
        <th mat-header-cell *matHeaderCellDef> Level </th>
        <td mat-cell *matCellDef="let element"> {{element.level}} </td>
      </ng-container>

      <!-- HP Column -->
      <ng-container matColumnDef="hp">
        <th mat-header-cell *matHeaderCellDef> HP </th>
        <td mat-cell *matCellDef="let element"> {{element.hp}} </td>
      </ng-container>

      <!-- Rarity Column -->
      <ng-container matColumnDef="rarity">
        <th mat-header-cell *matHeaderCellDef> Rarity </th>
        <td mat-cell *matCellDef="let element"> {{element.rarity}} </td>
      </ng-container>

      <!-- Rarity Column -->
      <ng-container matColumnDef="select">
        <th mat-header-cell *matHeaderCellDef> Select </th>
        <td mat-cell *matCellDef="let element">
          <button mat-button class="justify-items-center" (click)="addCardToDeck(element)">
            <mat-icon *ngIf="!element.isCardSelected" class="text-green-500">add_circle</mat-icon>
            <mat-icon *ngIf="element.isCardSelected" class="text-red-600">remove_circle</mat-icon>
          </button>
        </td>
      </ng-container>

      <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
