import { Component, OnInit } from '@angular/core';
import { Data } from 'src/app/interfaces/Icard';

import { Ideck } from 'src/app/interfaces/Ideck';
import { CardService } from '../cards/service/card.service';

import {MatTableDataSource} from '@angular/material/table';
import {SelectionModel} from '@angular/cdk/collections';

export interface PeriodicElement {
  name: string;
  position: number;
  weight: number;
  symbol: string;
}

const ELEMENT_DATA: PeriodicElement[] = [
  {position: 1, name: 'Hydrogen', weight: 1.0079, symbol: 'H'},
  {position: 2, name: 'Helium', weight: 4.0026, symbol: 'He'},
  {position: 3, name: 'Lithium', weight: 6.941, symbol: 'Li'},
  {position: 4, name: 'Beryllium', weight: 9.0122, symbol: 'Be'},
  {position: 5, name: 'Boron', weight: 10.811, symbol: 'B'},
  {position: 6, name: 'Carbon', weight: 12.0107, symbol: 'C'},
  {position: 7, name: 'Nitrogen', weight: 14.0067, symbol: 'N'},
  {position: 8, name: 'Oxygen', weight: 15.9994, symbol: 'O'},
  {position: 9, name: 'Fluorine', weight: 18.9984, symbol: 'F'},
  {position: 10, name: 'Neon', weight: 20.1797, symbol: 'Ne'},
];

@Component({
  selector: 'app-newdeckform',
  templateUrl: './newdeckform.component.html',
  styleUrls: ['./newdeckform.component.scss']
})

export class NewdeckFormComponent implements OnInit {

  public cards: Array<Data> = [];

  displayedColumns: string[] = ['images', 'name', 'supertype', 'level', 'hp', 'rarity', 'select'];
  dataSource = new MatTableDataSource<Data>();
  selection = new SelectionModel<Data>(true, []);

  constructor(private $cardsService: CardService) { }

  ngOnInit(): void {
    this.cards = this.$cardsService.allCards;
    this.dataSource = new MatTableDataSource<Data>(this.cards);
    console.log(this.$cardsService.allCards);
  }

    /** Whether the number of selected elements matches the total number of rows. */
    isAllSelected() {
      const numSelected = this.selection.selected.length;
      const numRows = this.cards.length;
      return numSelected === numRows;
    }

    /** Selects all rows if they are not all selected; otherwise clear selection. */
    toggleAllRows() {
      if (this.isAllSelected()) {
        this.selection.clear();
        return;
      }

      this.selection.select(...this.cards);
    }

    /** The label for the checkbox on the passed row */
    checkboxLabel(row?: Data): string {
      if (!row) {
        return `${this.isAllSelected() ? 'deselect' : 'select'} all`;
      }
      return `${this.selection.isSelected(row) ? 'deselect' : 'select'} row ${row.id + 1}`;
    }

    prevent($event: any) {
      $event.preventDefault();
      $event.stopPropagation();
    }

}
<div class="p-8 bg-amber-700">

  <!-- <mat-dialog-content class="mat-typography hidden">
    <h3>Name</h3>
    <p>{{this.deck.name}}</p>

    <h3>Number of cards</h3>
    <p>{{this.deck.cards.length}}</p>
  </mat-dialog-content>
  <mat-dialog-actions align="end">
    <button mat-button mat-dialog-close>Close</button>
  </mat-dialog-actions> -->


  <div class="grid grid-cols-1">

    <table mat-table [dataSource]="dataSource" class="mat-elevation-z8 w-full">

      <!-- Checkbox Column -->
      <ng-container matColumnDef="select">
        <th mat-header-cell *matHeaderCellDef>
          <mat-checkbox (change)="$event ? toggleAllRows() : null"
                        [checked]="selection.hasValue() && isAllSelected()"
                        [indeterminate]="selection.hasValue() && !isAllSelected()"
                        [aria-label]="checkboxLabel()">
          </mat-checkbox>
        </th>
        <td mat-cell *matCellDef="let row">
          <div (click)="prevent($event)">
            <mat-checkbox
              (change)="$event ? selection.toggle(row) : null"
              [checked]="selection.isSelected(row)"
              [aria-label]="checkboxLabel(row)">
            </mat-checkbox>
          </div>
        </td>
      </ng-container>

      <!-- Image Column -->
      <ng-container matColumnDef="images">
        <th mat-header-cell *matHeaderCellDef> Image </th>
        <td mat-cell *matCellDef="let element">
          <img class="rounded-full w-10 h-10" src="{{element.images.small}}" alt="{{element.name}}">
        </td>
      </ng-container>

      <!-- Name Column -->
      <ng-container matColumnDef="name">
        <th mat-header-cell *matHeaderCellDef> Name </th>
        <td mat-cell *matCellDef="let element"> {{element.name}} </td>
      </ng-container>

      <!-- Super Type Column -->
      <ng-container matColumnDef="supertype">
        <th mat-header-cell *matHeaderCellDef> Super Type </th>
        <td mat-cell *matCellDef="let element"> {{element.supertype}} </td>
      </ng-container>

      <!-- Level Column -->
      <ng-container matColumnDef="level">
        <th mat-header-cell *matHeaderCellDef> Level </th>
        <td mat-cell *matCellDef="let element"> {{element.level}} </td>
      </ng-container>

      <!-- HP Column -->
      <ng-container matColumnDef="hp">
        <th mat-header-cell *matHeaderCellDef> HP </th>
        <td mat-cell *matCellDef="let element"> {{element.hp}} </td>
      </ng-container>

      <!-- Rarity Column -->
      <ng-container matColumnDef="rarity">
        <th mat-header-cell *matHeaderCellDef> Rarity </th>
        <td mat-cell *matCellDef="let element"> {{element.rarity}} </td>
      </ng-container>

      <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
      <tr mat-row *matRowDef="let row; columns: displayedColumns;"
          (click)="selection.toggle(row)">
      </tr>
    </table>

  </div>



</div>
