import { HttpErrorResponse } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { CardService } from 'src/app/components/cards/service/card.service';
import { IStats } from 'src/app/interfaces/IStats';
import { IData, ICard } from 'src/app/interfaces/ICard';
import { IDeck } from 'src/app/interfaces/IDeck';
import { MatSnackBar } from '@angular/material/snack-bar';
import { SnackBarComponent } from '../snackBar/snack-bar.component';

@Component({
  selector: 'app-stats',
  templateUrl: './stats.component.html',
  styleUrls: ['./stats.component.scss']
})
export class StatsComponent implements OnInit {

  public allCards: ICard = {
    count: 0,
    data: [],
    page: 0,
    pageSize: 0,
    totalCount: 0
  };

  public decks: Array<IDeck> = [];

  public stats: IStats = {
    totalOfCards: 0,
    numberOfDecks: 0,
    numberOfCardsSelected: 0,
    totalHP: 0,
    highLevelCard: 0,
    totalPrice: 0
  };

  public userEmail: string = '';
  public componentName: string = '';

  public durationInSeconds: number = 5;

  constructor(
    private $cardsService: CardService,
    private _snackBar: MatSnackBar
  ) {}

  ngOnInit(): void {
    this.getAllCards();
    this.getCardsByUser();
  }

  getAllCards() {
    this.$cardsService.getAllCards().subscribe({
      next: (response: ICard) => {
        this.allCards = response;
        this.stats.totalOfCards = this.allCards.count;
      },
      error: (error: HttpErrorResponse) => {
        this.openSnackBar('Error retrieving Cards');
      }
    });
  }

  getCardsByUser() {
    this.userEmail = localStorage.getItem('login') || '';
    if(this.userEmail) {
      this.$cardsService.getCardsByUser(this.userEmail).subscribe({
        next: (response: Array<IDeck>) => {
          this.decks = response;
          this.stats.numberOfDecks = this.decks.length;
          this.getDeckStats(this.decks);
        },
        error: (error: HttpErrorResponse) => {
          this.openSnackBar('Error retrieving Cards');
        }
      });
    }
  }

  getDeckStats(decks: Array<IDeck>) {
    decks.forEach((deck: IDeck) => {
      this.stats.numberOfCardsSelected += deck.cards.length;
      deck.cards.forEach((card: IData) => {
        this.stats.totalHP += Number(card.hp);
        let currentLevel = Number(card['level']);
        this.stats.highLevelCard = currentLevel > this.stats.highLevelCard ? currentLevel : this.stats.highLevelCard;
        this.stats.totalPrice += card.cardmarket.prices.trendPrice;
      });
    });
  }

  openSnackBar(message: string) {
    this._snackBar.openFromComponent(SnackBarComponent, {
      duration: this.durationInSeconds * 1000,
      data: {message: message}
    });
  }
}




<div class="grid grid-cols-1 p-4" *ngIf="stats.totalOfCards > 0">
  <span class="font-bold text-3xl">Cards Stats</span>
</div>
<div class="grid space-x-1 lg:grid-cols-6 p-4 gap-2" *ngIf="stats.totalOfCards > 0">
  <div>
    <div class="bg-white">
      <div class="px-6 py-4 bg-gray-100 rounded">
        <div class="font-bold text-base mb-2 text-center">Total of Cards</div>
        <p class="text-sm text-center">
          <span class="font-normal text-4xl text-teal-500">
            {{ stats.totalOfCards ? stats.totalOfCards : 0 }}
          </span>
        </p>
      </div>
    </div>
  </div>
  <div>
    <div class="bg-white">
      <div class="px-6 py-4 bg-gray-100 rounded">
        <div class="font-bold text-xl mb-2 text-center">Number of Decks</div>
        <p class="text-sm text-center">
          <span class="font-normal text-4xl text-teal-500">
            {{ stats.numberOfDecks ? stats.numberOfDecks : 0 }}
          </span>
        </p>
      </div>
    </div>
  </div>
  <div>
    <div class="bg-white">
      <div class="px-6 py-4 bg-gray-100 rounded">
        <div class="font-bold text-xl mb-2 text-center">Number of Cards Selected</div>
        <p class="text-sm text-center">
          <span class="font-normal text-4xl text-teal-500">
            {{ stats.numberOfCardsSelected ? stats.numberOfCardsSelected : 0 }}
          </span>
        </p>
      </div>
    </div>
  </div>
  <div>
    <div class="bg-white">
      <div class="px-6 py-4 bg-gray-100 rounded">
        <div class="font-bold text-xl mb-2 text-center">Average HP</div>
        <p class="text-sm text-center">
          <span class="font-normal text-4xl text-teal-500">
            {{ stats.totalHP && stats.numberOfCardsSelected ? ((stats.totalHP / stats.numberOfCardsSelected) | number:'1.0-0') : 0.0 }}
          </span>
        </p>
      </div>
    </div>
  </div>
  <div>
    <div class="bg-white">
      <div class="px-6 py-4 bg-gray-100 rounded">
        <div class="font-bold text-xl mb-2 text-center">High Level Card</div>
        <p class="text-sm text-center">
          <span class="font-normal text-4xl text-teal-500">
            {{ stats.highLevelCard ? stats.highLevelCard : 0}}
          </span>
        </p>
      </div>
    </div>
  </div>
  <div>
    <div class="bg-white">
      <div class="px-6 py-4 bg-gray-100 rounded">
        <div class="font-bold text-xl mb-2 text-center">Card Average Price</div>
        <p class="text-sm text-center">
          <span class="font-normal text-4xl text-teal-500">
            € {{
              stats.totalPrice && stats.numberOfCardsSelected ?
              ((stats.totalPrice / stats.numberOfCardsSelected) | number:'1.0-2')
              : '0.00'
            }}
          </span>
        </p>
      </div>
    </div>
  </div>
</div>

<!-- loader -->
<div *ngIf="stats.totalOfCards <= 0" class="flex items-center justify-center h-screen">
  <div class="relative">
      <div class="h-24 w-24 rounded-full border-t-8 border-b-8 border-gray-200"></div>
      <div class="absolute top-0 left-0 h-24 w-24 rounded-full border-t-8 border-b-8 border-blue-500 animate-spin">
      </div>
  </div>
</div>
