import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent implements OnInit {

  public routesApp: Array<any> = [
    {
      id: 1,
      route: "/dashboard/deckofcards",
      active: false
    }
  ];

  public currentRoute: any = {
      id: -1,
      route: "",
      active: false
  };


  constructor(private router: Router) { }

  ngOnInit(): void {
    this.checkRoute();
  }

  checkRoute() {
    this.currentRoute = this.routesApp.find((r: any) => {
      r.active = true;
      return r.route === this.router.url;
    });
  }


}
<div class="flex w-screen h-screen text-gray-700">
  <app-leftmenu></app-leftmenu>
  <div class="flex flex-col flex-grow">
      <app-header></app-header>
      <h1 *ngIf="!currentRoute.active">Pokemon Dashboard Stats</h1>
      <app-deckofcards *ngIf="currentRoute ? currentRoute.active : false"></app-deckofcards>
      <router-outlet></router-outlet>
  </div>
</div>
