import { HttpErrorResponse } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { CardService } from 'src/app/components/cards/service/card.service';
import { IStats } from 'src/app/interfaces/IStats';
import { Data, ICard } from 'src/app/interfaces/Icard';
import { Ideck } from 'src/app/interfaces/Ideck';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent implements OnInit {

  public allCards: ICard = {
    count: 0,
    data: [],
    page: 0,
    pageSize: 0,
    totalCount: 0
  };

  public decks: Array<Ideck> = [];

  public routesApp: Array<any> = [
    {
      id: 1,
      route: "/dashboard/deckofcards",
      active: false
    }
  ];

  public currentRoute: any = {
      id: -1,
      route: "",
      active: false
  };

  public stats: IStats = {
    totalOfCards: 0,
    numberOfDecks: 0,
    numberOfCardsSelected: 0,
    averageHP: 0
  };

  public userEmail: string = '';

  constructor(
    private router: Router,
    private $cardsService: CardService
  ) {}

  ngOnInit(): void {
    this.getAllCards();
    this.getCardsByUser();
    this.checkRoute();
  }

  getAllCards() {
    this.$cardsService.getAllCards().subscribe({
      next: (response: ICard) => {
        this.allCards = response;
        this.stats.totalOfCards = this.allCards.count;
        //this.isLoading = false;
      },
      error: (error: HttpErrorResponse) => {
        //this.openSnackBar('Error retrieving Cards');
      }
    });
  }

  getCardsByUser() {
    this.userEmail = localStorage.getItem('login') || '';
    if(this.userEmail) {
      this.$cardsService.getCardsByUser(this.userEmail).subscribe((response: Array<Ideck>) => {
        this.decks = response;
        this.stats.numberOfDecks = this.decks.length;
        this.countOfSelectedCards(this.decks);
      });
    }
  }

  checkRoute() {
    this.currentRoute = this.routesApp.find((r: any) => {
      r.active = true;
      return r.route === this.router.url;
    });
  }

  countOfSelectedCards(decks: Array<Ideck>) {
    this.decks.forEach((deck: Ideck) => {
      this.stats.numberOfCardsSelected += deck.cards.length;
      deck.cards.forEach((card: Data) => {
        this.stats.totalHP += Number(card.hp);
      });
    });
  }

}
